import React, { useState } from "react";

import ToDoForm from "./ToDoForm";
import ToDo from "./ToDo";
import EditToDoForm from "./EditToDoForm";

import { ToDoInterface } from "../interface";

import { v4 as uuidv4 } from "uuid";

import { Alert } from "@mui/material";

const ToDoContainer = () => {
  // State to manage the list of todos
  const [todos, setTodos] = useState<ToDoInterface[]>([]);
  const [alert, setAlert] = useState(false);

  // Function to add a new todo
  const addTodo = (todo: string) => {
    //Check to see if user inputs only whitespace
    if (/^\s*$/.test(todo)){
      setAlert(true);
      // alert("Please enter a task")
      // <Alert severity="warning">Please enter a task!</Alert>
      return false;
    }
    const newTask: ToDoInterface = {
      id: uuidv4(), // Generate a unique ID for each todo
      task: todo,
      completed: false,
      isEditing: false,
    };
    setTodos([...todos, newTask]);
  };

  // Function to mark existing todo as completed
  const toggleComplete = (id: string) => {
    setTodos((prevTodos) =>
      prevTodos.map((todo) =>
        todo.id === id ? { ...todo, completed: !todo.completed } : todo,
      ),
    );
  };

  // Function to delete a todo
  const deleteTodo = (id: string) => {
    setTodos((prevTodos) => prevTodos.filter((todo) => todo.id !== id));
  };

  // Function to to toggle edit mode
  const editTodo = (id: string) => {
    setTodos((prevTodos) =>
      prevTodos.map((todo) =>
        todo.id === id ? { ...todo, isEditing: !todo.isEditing } : todo,
      ),
    );
  };

  // Function to update an existing todo
  const updateTodo = (task: string, id: string) => {
    setTodos((prevTodos) =>
      prevTodos.map((todo) =>
        todo.id === id ? { ...todo, task, isEditing: !todo.isEditing } : todo,
      ),
    );
  };

  return (
    <div className="TodoContainer">
      <h1>My To-Do List</h1>
      <ToDoForm addTodo={addTodo} />
      {alert && <Alert onClose={() => {}} severity="warning">Please enter a task!</Alert>}
      {/* Conditional rendering based on whether there are todos or not */}
      {todos.length === 0 ? (
        <p>No tasks have been added yet!</p>
      ) : (
        todos.map((todo) =>
          todo.isEditing ? (
            <EditToDoForm updateTodo={updateTodo} todo={todo} key={todo.id} />
          ) : (
            <ToDo
              todo={todo}
              key={todo.id}
              toggleComplete={toggleComplete}
              deleteTodo={deleteTodo}
              editTodo={editTodo}
            />
          ),
        )
      )}
    </div>
  );
};

export default ToDoContainer;
